import React, { useState } from "react";
import style from "./modal.module.css";
import KeyBoard from "../teclado/keyboard";
import Display from "../display/display";

const Modal = () => {
  const [isModalOpen, setIsModalOpen] = useState(true);
  const [displayValue, setDisplayValue] = useState("");

  const handleVerification = () => {
    // Verifica el código aquí y aplica la clase 'hidden' si es correcto
    if (displayValue === "0137") {
      console.log("¡Código correcto!");
      setIsModalOpen(false); // Esto aplicará la clase 'hidden'
    } else {
      alert("Código incorrecto. Intenta de nuevo.");
    }
  };

  const handleDelete = () => {
    // Lógica para borrar el último dígito
    setDisplayValue(displayValue.slice(0, -1));
  };
  const modalStyles = {
    display: isModalOpen ? 'flex' : 'none',
    // Otros estilos aquí si es necesario
  };

  return (
    <div className={`${style.containerModal} ${isModalOpen ? '' : style.hidden}`}>
      <h1>MODAL PRUEBA</h1>
      <KeyBoard onOk={handleVerification} onDelete={handleDelete} setDisplayValue={setDisplayValue} />
      <Display value={displayValue} />
    </div>
  );
};

export default Modal;
