export const validateName = (name) => {
    if (name.length > 3 && name !== "") {
      return true;
    } else {
      return false;
    }
  };
  
  export const validateLastName = (lastName) => {
    if (lastName.length > 3 && lastName !== "") {
      return true;
    } else {
      return false;
    }
  };
  
  export const validateEmail = (email) => {
    if (email.length > 3 && email !== "") {
      return true;
    } else {
      return false;
    }
  };
