import React from 'react';
import style from './cardDetails.module.css';
import { useParams } from 'react-router-dom';
import { useSelector } from 'react-redux';

const CardDetails = function (props) {
  const { id } = useParams();
  const character = useSelector(state => state.characters.find(char => char.id === id));

  if (!character) {
    return <div>Personaje no encontrado</div>;
  }

  return (
    <div className={style.container}>
      <button onClick={props.onClose} className={style.button}>X</button>
      <div>
        <div className={style.info}>
          <h2>Name: </h2>
          <p className={style.elementoP}>{character.name}</p>
        </div>
        <div className={style.info}>
          <h2>Status: </h2>
          <p className={style.elementoP}>{character.status}</p>
        </div>
        <div className={style.info}>
          <h2>Specie: </h2>
          <p className={style.elementoP}>{character.species}</p>
        </div>
        <div className={style.info}>
          <h2>Gender: </h2>
          <p className={style.elementoP}>{character.gender}</p>
        </div>
        <div className={style.info}>
          <h2>Origin: </h2>
          <p className={style.elementoP}>{character.origin.name}</p>
        </div>
      </div>
      <div>
        <img src={character.image} alt='Personaje' className={style.img} />
      </div>
      <div className={style.infoEpisodes}>
        <h2>Episodes: </h2>
        <p className={style.elementoP}>{character.episode.length}</p>
      </div>
    </div>
  );
};

export default CardDetails;
