import React from 'react';
import style from './switch.module.css';

const Switch = ({ darkMode, toggleDarkMode }) => {
    const handleToggle = () => {
        toggleDarkMode(); // Llama a la función toggleDarkMode para cambiar el modo de color
    };

    return (
        <div className={style.contSwitch}>
            <div>
                <span className={style.switchText}>Modo oscuro</span>
            </div>
            <label className={style.switch}>
                <input type="checkbox" className={style.input__check} checked={darkMode} onChange={handleToggle} />
                <span className={style.slider}>{darkMode ? 'ES' : 'EN'}</span>
            </label>
        </div>
    );
};

export default Switch;
