import { ADD_FAV, REMOVE_FAV, FILTER, ORDER } from "./Actions";
import { GET_CHARACTER_DETAILS } from './Actions';
const initialState = {
    myFavorites: [],
    characterDetail: {},
    allCharacters: []
};

function reducer(state = initialState, action) {
    switch (action.type) {
        case ADD_FAV:
            console.log(action.payload);
            console.log(state.myFavorites)
            return {
                ...state,
                myFavorites: [...state.allCharacters, action.payload]
            };
        case REMOVE_FAV:
            // Corregir el caso de eliminación de favoritos
            const filteredFavorites = state.myFavorites.filter((fav) => fav.id !== Number(action.payload));
            return {
                ...state,
                myFavorites: filteredFavorites
            };
        case GET_CHARACTER_DETAILS:
            return {
                ...state,
                myFavorites: [...state.myFavorites, action.payload]
            }
        case FILTER:
            return {
                ...state,
                myFavorites: action.payload
            }
        case ORDER:
            return {
                ...state,
                myFavorites: action.payload
            }
        
        default:
            return state;
    }
};

export default reducer;
