const initialState = {
    myFavorites: []
};

function reducer(state = initialState, action) {
    switch (action.type) {
        case "ADD_FAV":
            return {
                ...state,
                myFavorites: [...state.myFavorites, action.payload],
                allCharacters: [...state.allCharacters, action.payload]
            };
        case "REMOVE_FAV":
            // Corregir el caso de eliminación de favoritos
            const filtered = state.myFavorites.filter((fav) => fav.id !== Number(action.payload));
            return {
                ...state,
                myFavorites: filtered
            };
        default:
            return state;
    }
};

export default reducer;
