import React from 'react';
import style from './card.module.css';
import { addFav, removeFav } from '../../Redux/Actions.js';

const Card = function (props) {
   const [isFav, setIsFav] = React.useState(false);
   React.useEffect(() => {
      setIsFav(props.myFavorites.includes(props.id));
   }, [props.myFavorites, props.id]);

   const handleFavorite = () => {
      if (isFav) {
         props.removeFavorite(props.id);
         setIsFav(false); // Cambiar el estado local
      } else {
         props.addFavorite(props);
         setIsFav(true); // Cambiar el estado local
      }
   }

   return (
      <div className={style.contariner}>
         <div>
            <div>
               <button onClick={handleFavorite}>
                  {isFav ? '‚ù§Ô∏è' : 'ü§ç'} {/* Usar isFav para mostrar el coraz√≥n lleno o vac√≠o */}
               </button>
            </div>
            <div className={style.info}>
               <h2>Name: </h2>
               <p className={style.elementoP}>{props.name}</p>
            </div>
            <div className={style.info}>
               <h2>Status: </h2>
               <p className={style.elementoP}>{props.status}</p>
            </div>
         </div>
         <div>
            <img src={props.image} alt='Personaje' className={style.img} />
         </div>
         <div className={style.infoEpisodes}>
            <button className={style.button}>
               <span className={style.buttonText}>View more</span>
            </button>
         </div>
      </div>
   );
}
export const mapDispatchToProps = (dispatch) => {
   return {
      addFavorite: (character) => {
         dispatch(addFavorite(character));
      },
      removeFavorite: (id) => {
         dispatch(removeFavorite(id));
      },
   };
}
export default Card;
