import { createStore, applyMiddleware, compose } from 'redux';
import rootReducer from './reducer.js';
import { thunk } from 'redux-thunk'; // Importa 'thunk' desde redux-thunk
import { persistStore, persistReducer } from 'redux-persist';
import storage from 'redux-persist/lib/storage'; // Esto importará el almacenamiento local por defecto para la web
const persistConfig = {
    key: 'root',
    storage,
  };
  
  const persistedReducer = persistReducer(persistConfig, rootReducer);
const composeEnhancers = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;
export const store = createStore(rootReducer, persistStore, composeEnhancers(applyMiddleware(thunk)));
