const Card = function (props) {
   const [isFav, setIsFav] = useState(false);
   const dispatch = useDispatch();

   useEffect(() => {
      props.myFavorites.forEach((fav) => {
         if (fav.id === props.id) {
            setIsFav(true);
         }
      });
   }, [props.myFavorites]);

   const handleFavorite = () => {
      if (isFav) {
         dispatch(removeFav(props.id)); // Llama a la acción removeFav usando dispatch
      } else {
         dispatch(addFav({ // Llama a la acción addFav usando dispatch
            id: props.id,
            name: props.name,
            status: props.status,
            species: props.species,
            image: props.image,
            gender: props.gender
         }));
      }
      setIsFav(!isFav);
   };

   return (
      <div className={style.container}>
         <div>
            <div>
               <button onClick={handleFavorite} className={style.btnFav}>
                  {isFav ? '❤️' : '🤍'} {/* Usar isFav para mostrar el corazón lleno o vacío */}
               </button>
            </div>
            <div className={style.info}>
               <h2>Name: </h2>
               <p className={style.elementoP}>{props.name}</p>
            </div>
            <div className={style.info}>
               <h2>Status: </h2>
               <p className={style.elementoP}>{props.status}</p>
            </div>
         </div>
         <div>
            <img src={props.image} alt='Personaje' className={style.img} />
         </div>
         <div className={style.infoEpisodes}>
            <button className={style.button}>
               <span className={style.buttonText}>View more</span>
            </button>
         </div>
      </div>
   );
}
